        for i in range(len(mot_secret)):
            if mot_secret[i] in lettres_correctes:
                PL_devinerda += mot_secret[i]
            else:
                PL_devinerda += '_'

        # Affiche le mot secret avec des espaces entre chaque mot
        for palabra in PL_devinerda:
            print(palabra, end=' ')
        print()

